
public class SwitchText {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<=3;i++){
			switch (i) {
			case 0:
				System.out.println("0");
				break;
			case 1:
				System.out.println("1");
				break;
			case 2:
				System.out.println("2");
				break;
			default:
				System.out.println("3");
				break;
			}
		}
	}

}
